test_list_available_calculators()

import requests
from requests.auth import HTTPBasicAuth

BASE_URL = "http://localhost:80"
AUTH = HTTPBasicAuth("uniquebishwo@gmail.com", "c9PU7XAsAADYk_A")
TIMEOUT = 30

def test_execute_calculator_function():
    headers = {"Content-Type": "application/json"}

    # Test valid calculation
    valid_module = "electrical"
    valid_function = "add"  # Hypothetical existing function
    valid_payload = {
        "input_values": {
            "a": 5,
            "b": 3
        }
    }
    url_valid = f"{BASE_URL}/calculator/{valid_module}/{valid_function}"
    try:
        response_valid = requests.post(url_valid, json=valid_payload, headers=headers, auth=AUTH, timeout=TIMEOUT)
    except requests.RequestException as e:
        assert False, f"Request failed for valid calculation: {e}"

    assert response_valid.status_code == 200, f"Expected 200 OK for valid calculation, got {response_valid.status_code}"
    try:
        result = response_valid.json()
    except ValueError:
        assert False, "Response for valid calculation is not valid JSON"

    assert "result" in result, "Response JSON does not contain 'result' key for valid calculation"
    assert isinstance(result["result"], (int, float)), "'result' should be a number"

    # Test calculator not found
    invalid_module = "nonexistentmodule"
    url_invalid_module = f"{BASE_URL}/calculator/{invalid_module}/{valid_function}"
    try:
        response_invalid_module = requests.post(url_invalid_module, json=valid_payload, headers=headers, auth=AUTH, timeout=TIMEOUT)
    except requests.RequestException as e:
        assert False, f"Request failed for invalid calculator module: {e}"

    assert response_invalid_module.status_code == 404, f"Expected 404 Not Found for invalid calculator module, got {response_invalid_module.status_code}"

    # Test function not found
    invalid_function = "nonexistentfunction"
    url_invalid_function = f"{BASE_URL}/calculator/{valid_module}/{invalid_function}"
    try:
        response_invalid_function = requests.post(url_invalid_function, json=valid_payload, headers=headers, auth=AUTH, timeout=TIMEOUT)
    except requests.RequestException as e:
        assert False, f"Request failed for invalid function: {e}"

    assert response_invalid_function.status_code == 404, f"Expected 404 Not Found for invalid function, got {response_invalid_function.status_code}"

test_execute_calculator_function()

import requests
from requests.auth import HTTPBasicAuth

BASE_URL = "http://localhost:80"
AUTH_CREDENTIALS = ("uniquebishwo@gmail.com", "c9PU7XAsAADYk_A")

def test_get_user_profile_data():
    profile_url = f"{BASE_URL}/profile"
    timeout = 30

    # Authenticated request
    try:
        auth_response = requests.get(profile_url, auth=HTTPBasicAuth(*AUTH_CREDENTIALS), timeout=timeout)
    except requests.RequestException as e:
        assert False, f"Authenticated request failed with exception: {e}"
    else:
        assert auth_response.status_code == 200, f"Expected 200 OK for authenticated request but got {auth_response.status_code}"
        try:
            profile_data = auth_response.json()
        except ValueError:
            assert False, "Response from authenticated request is not valid JSON"
        else:
            # Validate expected fields in profile data (at least it should be a dict)
            assert isinstance(profile_data, dict), "Profile data should be a JSON object"
            # Can add more validations if schema known, e.g. keys "first_name", "last_name", "email"
            assert 'email' in profile_data or 'username' in profile_data or len(profile_data) > 0, "Profile data seems empty or missing expected keys"

    # Unauthenticated request
    try:
        unauth_response = requests.get(profile_url, timeout=timeout)
    except requests.RequestException as e:
        assert False, f"Unauthenticated request failed with exception: {e}"
    else:
        assert unauth_response.status_code == 401, f"Expected 401 Unauthorized for unauthenticated request but got {unauth_response.status_code}"

test_get_user_profile_data()
